import{c as s}from"./app.43fe0c63.js";import{_ as n}from"./plugin-vue_export-helper.21dcd24c.js";const a={},e=s(`<h1 id="sql-\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#sql-\u57FA\u7840" aria-hidden="true">#</a> SQL \u57FA\u7840</h1><p>SQL <code>/\u02C8si\u02D0kw\u0259l/</code>\uFF0CStructured Query Language\uFF0C\u7ED3\u6784\u5316\u67E5\u8BE2\u8BED\u8A00\u662F\u7528\u4E8E\u4E0E<strong>\u5173\u7CFB\u578B\u6570\u636E\u5E93</strong>\u8FDB\u884C\u4EA4\u4E92\u7684\u7F16\u7A0B\u8BED\u8A00\u3002</p><p>\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5B58\u50A8\u7684<strong>\u6570\u636E\u53D7\u4E00\u5B9A\u7684\u89C4\u8303\u9650\u5236</strong>\u4EE5\u5B9E\u73B0\u7279\u5B9A\u7684\u7ED3\u6784\u4FBF\u4E8E\u67E5\u8BE2</p><ul><li>\u6570\u636E\u5B58\u50A8\u5728\u7C7B\u4F3C\u4E8E Excel \u7684\u7535\u5B50<strong>\u8868\u683C</strong>\u4E2D\uFF0C<strong>\u6BCF\u5217 column</strong>\u8868\u793A\u4E00\u79CD\u5C5E\u6027\uFF0C\u5982\u540D\u79F0\u3001\u4F4D\u7F6E\u3001\u552F\u4E00\u8EAB\u4EFD\u7B49\uFF0C<strong>\u6BCF\u884C row</strong>\u8868\u793A\u4E00\u6761\u6570\u636E</li><li>\u540C\u4E00\u5217\u4E2D\u7684\u6240\u6709\u6570\u636E\u5FC5\u987B\u4E3A<strong>\u4E00\u81F4\u7684\u6570\u636E\u7C7B\u578B</strong>\uFF0C\u8FD9\u662F\u6570\u636E\u5E93\u53EF\u4EE5\u5FEB\u901F\u4F7F\u7528\u7684\u539F\u56E0\u4E4B\u4E00</li></ul><p>\u{1F4A1} \u6709\u4E00\u79CD\u79F0\u4E3A NoSQL \u6570\u636E\u5E93\u4EA4\u4E92\u8BED\u8A00\uFF0C\u8868\u793A Not only SQL\uFF08\u4E0D\u4EC5\u4EC5\u662F SQL\uFF09\uFF0C\u5B83\u66F4\u9002\u7528\u4E8E<strong>\u57FA\u4E8E\u7F51\u7EDC\u6570\u636E\u7684\u73AF\u5883</strong>\uFF0C\u800C\u4E0D\u592A\u9002\u7528\u4E8E\u57FA\u4E8E\u7535\u5B50\u8868\u683C\u7684\u6570\u636E\u5206\u6790\uFF0C\u6700\u5E38\u7528\u7684 NoSQL \u6570\u636E\u5E93\u4E4B\u4E00\u662F <a href="https://www.mongodb.com/" target="_blank" rel="noopener noreferrer">MongoDB</a>\u3002</p><h2 id="\u89C4\u8303" tabindex="-1"><a class="header-anchor" href="#\u89C4\u8303" aria-hidden="true">#</a> \u89C4\u8303</h2><p>\u7F16\u5199 SQL \u811A\u672C\u9700\u8981\u9075\u5FAA\u4E00\u5B9A\u7684\u89C4\u8303</p><ul><li><p>\u5927\u5C0F\u5199\uFF1ASQL \u4E0D\u533A\u5206\u5927\u5C0F\u5199\uFF0C\u4F46\u662F\u4E00\u822C\u9075\u5FAA<strong>\u547D\u4EE4\u8BED\u53E5\u5927\u5199\uFF0C\u800C\u5176\u4ED6\u5185\u5BB9\u5C0F\u5199\u7684\u89C4\u8303</strong>\uFF0C\u8FD9\u4F7F\u5F97\u811A\u672C\u66F4\u5BB9\u6613\u9605\u8BFB</p></li><li><p>\u7A7A\u683C\uFF1A\u5728\u8868\u3001\u5217\u540D\u6216\u53D8\u91CF\u540D\u4E2D<strong>\u907F\u514D</strong>\u4F7F\u7528\u7A7A\u683C \uFF08\u5426\u5219\u4F1A\u88AB\u8BA4\u4E3A\u662F\u4E24\u4E2A\u4E0D\u540C\u7684\u53D8\u91CF\uFF09\uFF0C\u5982\u679C\u7531\u591A\u4E2A\u5355\u8BCD\u6784\u6210\u5E94\u8BE5\u4F7F\u7528 <code>_</code> \u4E0B\u5212\u7EBF\u5206\u9694\u3002\u5982\u679C\u8868\u540D\u6216\u5217\u540D\u79F0\u4E2D\u6709\u7A7A\u683C\u9700\u8981\u4F7F\u7528 <code>&quot;&quot;</code> \u53CC\u5F15\u53F7\u62EC\u4F4F\uFF08\u5728\u5176\u4ED6\u73AF\u5883\u4E2D\u53EF\u80FD\u4F1A\u4F7F\u7528\u65B9\u62EC\u53F7\uFF0C\u5982 <code>FROM [Table Name]</code>\uFF09\u3002 \u{1F4A1} \u7531\u4E8E SQL \u67E5\u8BE2\u8BED\u53E5\u4F1A\u5FFD\u7565\u7A7A\u683C\uFF0C\u53EF\u4EE5\u6839\u636E\u9700\u8981\u5728\u67E5\u8BE2\u4EE3\u7801\u4E2D\u6DFB\u52A0\u7A7A\u683C\uFF0C\u4F7F\u4EE3\u7801\u4FBF\u4E8E\u9605\u8BFB</p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> account_id <span class="token keyword">FROM</span> orders
<span class="token comment">-- \u7B49\u4EF7\u4E8E\u8FD9\u4E2A\u67E5\u8BE2:</span>

<span class="token keyword">SELECT</span> account_id
<span class="token keyword">FROM</span> orders
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div></li><li><p>\u5206\u53F7\uFF1A\u590D\u5408\u67E5\u8BE2\u8BED\u53E5\u7684\u7ED3\u5C3E\u9700\u8981\u6DFB\u52A0 <code>;</code> \u5206\u53F7\uFF0C\u8868\u793A\u8BE5\u8BED\u53E5\u7ED3\u675F</p></li></ul><p>SQL \u5E38\u7528\u7684\u8BED\u53E5\u548C\u8FD0\u7B97\u7B26</p><table><thead><tr><th><strong>\u8BED\u53E5\u6216\u8FD0\u7B97\u7B26</strong></th><th><strong>\u4F7F\u7528\u65B9\u6CD5</strong></th><th><strong>\u5176\u4ED6\u8BE6\u7EC6\u4FE1\u606F</strong></th></tr></thead><tbody><tr><td><code>SELECT</code></td><td><code>SELECT Col1, Col2</code></td><td>\u63D0\u4F9B\u4F60\u9700\u8981\u7684\u5217</td></tr><tr><td><code>FROM</code></td><td><code>FROM Table</code></td><td>\u63D0\u4F9B\u5217\u6240\u5728\u7684\u8868\u683C</td></tr><tr><td><code>LIMIT</code></td><td><code>LIMIT 10</code></td><td>\u9650\u5236\u8FD4\u56DE\u7684\u884C\u6570</td></tr><tr><td><code>ORDER BY</code></td><td><code>ORDER BY Col</code></td><td>\u6839\u636E\u6307\u5B9A\u7684\u5217\u5BF9\u8868\u683C\u6392\u5E8F\uFF0C\u53EF\u4EE5\u6DFB\u52A0 <code>DESC</code> \u5B9E\u73B0\u964D\u5E8F\u6392\u5217</td></tr><tr><td><code>WHERE</code></td><td><code>WHERE Col &gt; 5</code></td><td>\u7528\u4E8E\u8FC7\u6EE4\u7ED3\u679C\u7684\u4E00\u4E2A\u6761\u4EF6\u8BED\u53E5</td></tr><tr><td><code>LIKE</code></td><td><code>WHERE Col LIKE &#39;%me%&#39;</code></td><td>\u4EC5\u63D0\u53D6\u51FA\u5217\u6587\u672C\u4E2D\u5177\u6709 <code>me</code> \u7684\u884C</td></tr><tr><td><code>IN</code></td><td><code>WHERE Col IN (&#39;Y&#39;, &#39;N&#39;)</code></td><td>\u4EC5\u8FC7\u6EE4\u884C\u5BF9\u5E94\u7684\u5217\u7684\u503C\u4E3A <code>Y</code> \u6216 <code>N</code></td></tr><tr><td><code>NOT</code></td><td><code>WHERE Col NOT IN (&#39;Y&#39;, &#39;N&#39;)</code></td><td><code>NOT</code> \u7ECF\u5E38\u4E0E <code>LIKE</code> \u548C <code>IN</code> \u4E00\u8D77\u4F7F\u7528</td></tr><tr><td><code>AND</code></td><td><code>WHERE Col1 &gt; 5 AND Col2 &lt; 3</code></td><td>\u8FC7\u6EE4\u4E24\u4E2A\u6216\u591A\u4E2A\u6761\u4EF6\u5FC5\u987B\u4E3A\u771F\u7684\u884C</td></tr><tr><td><code>OR</code></td><td><code>WHERE Col1 &gt; 5 OR Col2 &lt; 3</code></td><td>\u8FC7\u6EE4\u5176\u4E2D\u4E00\u4E2A\u6761\u4EF6\u4E3A\u771F\u7684\u884C</td></tr><tr><td><code>BETWEEN</code></td><td><code>WHERE Col BETWEEN 3 AND 5</code></td><td>\u4E00\u822C\u60C5\u51B5\u4E0B\uFF0C\u8BED\u6CD5\u6BD4\u4F7F\u7528 <code>AND</code> \u7B80\u5355\u4E00\u4E9B</td></tr></tbody></table><p>\u{1F4A1} \u867D\u7136 SQL <strong>\u4E0D\u533A\u5206\u5927\u5C0F\u5199</strong>\uFF08\u8BED\u53E5\u65E0\u6240\u8C13\u5927\u5C0F\u5199\uFF09\uFF0C\u4F46\u5173\u952E\u8BCD\u7684<strong>\u987A\u5E8F</strong>\u5F88\u91CD\u8981</p><h2 id="\u5E38\u7528\u8BED\u53E5" tabindex="-1"><a class="header-anchor" href="#\u5E38\u7528\u8BED\u53E5" aria-hidden="true">#</a> \u5E38\u7528\u8BED\u53E5</h2><p><code>.sql</code> \u811A\u672C\u4E3B\u8981\u7531\u5404\u79CD\u8BED\u53E5 clause \u6784\u6210<strong>\u7B5B\u9009\u6761\u4EF6</strong>\uFF0C\u7528\u4EE5\u9009\u62E9\u7279\u5B9A\u7684\u6570\u636E\u3002</p><h3 id="create" tabindex="-1"><a class="header-anchor" href="#create" aria-hidden="true">#</a> CREATE</h3><p><code>CREATE TABLE</code> \u5728\u6570\u636E\u5E93\u4E2D\u521B\u5EFA\u65B0\u8868</p><h3 id="drop" tabindex="-1"><a class="header-anchor" href="#drop" aria-hidden="true">#</a> DROP</h3><p><code>DROP TABLE</code> \u5220\u9664\u6570\u636E\u5E93\u4E2D\u8868</p><h3 id="select-from" tabindex="-1"><a class="header-anchor" href="#select-from" aria-hidden="true">#</a> SELECT-FROM</h3><p>\u67E5\u8BE2\u8BED\u53E5\u7528\u4E8E\u9009\u62E9\u9700\u8981\u8BFB\u53D6\u5E76\u663E\u793A\u7684\u6570\u636E\uFF08\u4E00\u822C\u662F\u9009\u62E9\u663E\u793A\u7279\u5B9A\u8868\u683C\u7684\u7279\u5B9A\uFF08\u591A\uFF09\u5217\uFF09\uFF0C\u7531\u4E24\u90E8\u5206 <code>SELECT</code> \u548C <code>FROM</code> \u7EC4\u6210</p><ul><li><code>SELECT</code> \u7528\u4E8E\u9009\u62E9\u9700\u8981\u67E5\u8BE2\u7684<strong>\u5217</strong>\uFF0C\u53EF\u540C\u65F6\u9009\u62E9\u591A\u5217\uFF0C\u5217\u540D\u4EE5\u9017\u53F7 <code>,</code> \u9694\u5F00\uFF08\u6700\u540E\u4E00\u4E2A\u5217\u540D\u65E0\u9700\u52A0\u9017\u53F7\uFF09 \u{1F4A1} \u82E5\u9009\u62E9\u6240\u6709\u5217\u53EF\u4F7F\u7528\u901A\u914D\u7B26 <code>*</code></li><li><code>FROM</code> \u8BED\u53E5\u7528\u4E8E\u6307\u660E\u6570\u636E\u6765\u6E90\uFF0C\u5373\u9700\u8981\u9009\u62E9\u7684\u5217\u6765\u6E90\u4E8E\u54EA\u4E2A\u8868\u683C</li></ul><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token comment">-- \u9009\u62E9 orders \u8868\u683C\u4E2D\u7684 id\u3001account_id\u3001occurred_at \u5217</span>
<span class="token keyword">SELECT</span> id<span class="token punctuation">,</span> account_id<span class="token punctuation">,</span> occurred_at <span class="token keyword">FROM</span> orders

<span class="token comment">-- \u9009\u62E9 orders \u8868\u683C\u4E2D\u7684\u6240\u6709\u5217</span>
<span class="token keyword">SELECT</span> <span class="token operator">*</span> <span class="token keyword">FROM</span> table_name
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p>\u{1F4A1} \u4F7F\u7528 <code>SELECT DISTINCT</code> \u4F7F\u7ED3\u679C\u4EC5\u663E\u793A\u552F\u4E00\u7684\u503C\uFF08\u9009\u62E9\u663E\u793A\u7684\u5B57\u6BB5\u4E2D\uFF0C<mark>\u6CA1\u6709\u4E24\u884C\u6570\u636E\u662F\u5B8C\u5168\u76F8\u540C\u7684</mark>\uFF09</p><h3 id="where" tabindex="-1"><a class="header-anchor" href="#where" aria-hidden="true">#</a> WHERE</h3><p><code>WHERE</code> \u4E3A\u8868\u683C\u521B\u5EFA\u5B50\u96C6\uFF0C\u540E\u9762\u6DFB\u52A0\u6761\u4EF6\u8BED\u53E5\u4EE5<strong>\u7B5B\u9009</strong>\u51FA\u6EE1\u8DB3\u7279\u5B9A\u6761\u4EF6\u7684<strong>\u6570\u636E\u5B50\u96C6</strong></p><p>\u5728\u6761\u4EF6\u8BED\u53E5\u4E2D\u5E38\u7528\u7684\u8FD0\u7B97\u7B26</p><ul><li><code>&gt;</code> \u5927\u4E8E</li><li><code>&lt;</code> \u5C0F\u4E8E</li><li><code>&gt;=</code> \u5927\u4E8E\u6216\u7B49\u4E8E</li><li><code>&lt;=</code> \u5C0F\u4E8E\u6216\u7B49\u4E8E</li><li><code>=</code> \u7B49\u4E8E</li><li><code>!=</code> \u4E0D\u7B49\u4E8E</li></ul><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> <span class="token operator">*</span> <span class="token keyword">FROM</span> orders
<span class="token keyword">WHERE</span> gloss_amt_usd <span class="token operator">&gt;=</span> <span class="token number">1000</span>
LIMI <span class="token number">5</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>\u8FD0\u7B97\u7B26 <code>=</code> \u548C <code>!=</code> \u9664\u4E86\u53EF\u5E94\u7528\u4E8E\u6570\u503C\u6570\u636E\u5916\uFF0C\u4E5F\u53EF\u4EE5\u7528\u4E8E\u975E\u503C\u6570\u636E\uFF08\u5982\u5B57\u7B26\u4E32\uFF09\uFF0C\u26A0\uFE0F \u5982\u679C\u4F7F\u7528\u6587\u672C\u6570\u636E\u65F6\uFF0C\u8981\u4F7F\u7528\u5F15\u53F7 <code>&#39;&#39;</code> \u6216 <code>&quot;&quot;</code> \u5C06\u503C\u62EC\u8D77\u6765\uFF08\u63A8\u8350\u4F7F\u7528\u5355\u5F15\u53F7\uFF0C\u7279\u522B\u662F\u539F\u59CB\u6570\u636E\u4E2D\u5982\u679C\u4F7F\u7528\u4E86\u53CC\u5F15\u53F7\u7684\u60C5\u51B5\u4E0B\uFF09</p><h3 id="order-by" tabindex="-1"><a class="header-anchor" href="#order-by" aria-hidden="true">#</a> ORDER BY</h3><p><code>ORDER BY</code> \u8BED\u53E5\u6309\u4E00\u5B9A\u7684\u987A\u5E8F\u6392\u5E8F\u8868</p><ul><li><code>ORDER BY</code> \u8BED\u53E5\u59CB\u7EC8\u5728 <code>SELECT</code> \u548C <code>FROM</code> \u8BED\u53E5\u4E4B\u540E</li><li><code>ORDER BY</code> \u9ED8\u8BA4\u6309\u5347\u5E8F ascending \u6392\u5217\uFF0C\u5373\u6309\u7167\u6570\u5B57\u4ECE\u5927\u5230\u5C0F\u3001\u5B57\u6BCD\u4ECE a \u5230 z\u3001\u65F6\u95F4\u4ECE\u65E9\u5230\u665A\u7B49\uFF0C\u{1F4A1} \u53EF\u4EE5\u5728 <code>ORDER BY</code> \u540E\u6DFB\u52A0 <code>DESC</code> \u6309\u964D\u5E8F\u6392\u5E8F</li><li>\u5982\u679C <code>ORDER BY</code> \u540E\u6DFB\u52A0\u591A\u4E2A\u5B57\u6BB5/\u5C5E\u6027\uFF0C\u5219\u4F1A\u6309\u7167\u4ECE\u5DE6\u5F80\u53F3\u6307\u5B9A\u7684\u5217\u7684\u5148\u540E\u4F18\u5148\u6B21\u5E8F\uFF0C\u5BF9\u6570\u636E\uFF08\u8868\u683C\u7684\u884C\uFF09\u8FDB\u884C\u6392\u5E8F</li></ul><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> id<span class="token punctuation">,</span> account_id<span class="token punctuation">,</span> total
<span class="token keyword">FROM</span> orders
<span class="token keyword">ORDER</span> <span class="token keyword">BY</span> total_amt_usd <span class="token keyword">DESC</span>
<span class="token keyword">LIMIT</span> <span class="token number">20</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="limit" tabindex="-1"><a class="header-anchor" href="#limit" aria-hidden="true">#</a> LIMIT</h3><p><code>LIMIT</code> \u8BED\u53E5\u7528\u4E8E\u67E5\u770B\u8868\u7684\u524D\u51E0\u884C\uFF0C\u540E\u9762\u53C2\u6570\u89C4\u5B9A\u4E86\u67E5\u770B\u7684\u884C\u6570\uFF0C\u901A\u8FC7\u9650\u5236\u52A0\u8F7D\u884C\u6570\u53EF\u4EE5\u7F29\u77ED\u67E5\u8BE2\u7ED3\u679C\u7684\u8FD4\u56DE\u65F6\u95F4\uFF0C\u66F4\u5FEB\u5730<strong>\u9884\u89C8\u6570\u636E</strong></p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">LIMIT</span> int_number
</code></pre><div class="line-numbers"><span class="line-number">1</span><br></div></div><p>\u26A0\uFE0F <code>LIMIT</code> \u547D\u4EE4\u59CB\u7EC8\u662F\u6240\u6709\u67E5\u8BE2\u8BED\u53E5\u7684\u6700\u540E\u4E00\u90E8\u5206</p><h2 id="\u6D3E\u751F\u5217" tabindex="-1"><a class="header-anchor" href="#\u6D3E\u751F\u5217" aria-hidden="true">#</a> \u6D3E\u751F\u5217</h2><p>\u5728\u9009\u62E9\u8BED\u53E5\u4E2D\u53EF\u4EE5<strong>\u57FA\u4E8E\u73B0\u6709\u7684\u5217\u7EC4\u5408\uFF08\u901A\u8FC7<mark>\u7B97\u672F\u8FD0\u7B97</mark>\uFF09\u751F\u6210\u7684\u65B0\u5217</strong> <code>SELECT derive_column FROM table_name</code>\uFF0C\u8FD9\u4E9B\u5217\u79F0\u4E3A\u6D3E\u751F\u5217</p><p>\u5E38\u89C1\u8FD0\u7B97\u5305\u62EC\uFF1A</p><ul><li><code>*</code> \u4E58\u6CD5</li><li><code>+</code> \u52A0\u6CD5</li><li><code>-</code> \u51CF\u6CD5</li><li><code>/</code> \u9664\u6CD5</li></ul><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> id<span class="token punctuation">,</span>
       account_id<span class="token punctuation">,</span>
       <span class="token comment">-- \u6D3E\u751F\u5217</span>
       standard_amt_usd <span class="token operator">/</span> standard_qty <span class="token keyword">AS</span> unit_price
<span class="token keyword">FROM</span> orders
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p>\u{1F4A1} \u53EF\u4EE5\u4E3A\u6D3E\u751F\u5217\u547D\u540D\uFF0C\u5728\u8FD0\u7B97\u5F0F\u540E<strong>\u4F7F\u7528 <code>AS</code> \u8BED\u53E5\u547D\u540D</strong></p><h2 id="\u903B\u8F91\u8FD0\u7B97\u7B26" tabindex="-1"><a class="header-anchor" href="#\u903B\u8F91\u8FD0\u7B97\u7B26" aria-hidden="true">#</a> \u903B\u8F91\u8FD0\u7B97\u7B26</h2><p>\u5728 SQL \u8BED\u53E5\u4E2D\u53EF\u4EE5<strong>\u7528\u903B\u8F91\u8FD0\u7B97\u7B26\u7ED3\u5408\u5339\u914D\u8BED\u53E5 <code>WHERE</code> \u6784\u5EFA\u590D\u6742\u7684\u67E5\u8BE2\u7B5B\u9009\u547D\u4EE4</strong>\uFF0C\u4E00\u822C\u7528\u4E8E\u64CD\u4F5C\u6587\u672C\u6570\u636E\u3002</p><p>\u{1F4A1} \u8FD8\u53EF\u4EE5\u4F7F\u7528\u62EC\u53F7 <code>()</code> \u6765\u89C4\u5B9A\u6267\u884C\u8FD0\u7B97\u7684<strong>\u5148\u540E\u987A\u5E8F</strong></p><h3 id="like-\u6A21\u7CCA\u5339\u914D" tabindex="-1"><a class="header-anchor" href="#like-\u6A21\u7CCA\u5339\u914D" aria-hidden="true">#</a> LIKE \u6A21\u7CCA\u5339\u914D</h3><p><code>LIKE</code> \u8FD0\u7B97\u7B26\u7528\u4E8E\u6A21\u7CCA\u67E5\u627E</p><ul><li><code>LIKE</code> \u8FD0\u7B97\u7B26\u5904\u7406\u6587\u672C\u5E38\u4E0E\u901A\u914D\u7B26 <code>%</code> \u4E00\u8D77\u4F7F\u7528\u3002 <strong><code>%</code> \u9002\u914D\u4EFB\u610F\u6570\u91CF\u7684\u5B57\u7B26</strong>\uFF0C\u53EF\u4EE5\u4EA7\u751F\u4E00\u7EC4\u7279\u5B9A\u7684\u5339\u914D\u6A21\u5F0F\uFF08\u4E0E\u6B63\u5219\u8868\u8FBE\u5F0F\u7C7B\u4F3C\uFF09</li><li>\u9700\u8981\u7528\u5355\u5F15\u53F7 <code>&#39;&#39;</code> \u6216\u53CC\u5F15\u53F7 <code>&quot;&quot;</code> \u5305\u542B\u7740\u4F20\u8FBE\u7ED9 <code>LIKE</code> \u7684\u6587\u672C\uFF08\u5B57\u7B26\u4E32<strong>\u5BF9\u4E8E\u5927\u5C0F\u5199\u654F\u611F</strong>\uFF0C\u5982 \u641C\u7D22 <code>&#39;T&#39;</code> \u4E0E\u641C\u7D22 <code>&#39;t&#39;</code> \u4E0D\u540C\uFF09</li></ul><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> id<span class="token punctuation">,</span> name
<span class="token keyword">FROM</span> accounts
<span class="token comment">-- \u7B5B\u9009\u51FA\u5B57\u6BB5 name \u4EE5 &#39;s&#39; \u7ED3\u5C3E\u7684\u6570\u636E</span>
<span class="token keyword">WHERE</span> name <span class="token operator">LIKE</span> <span class="token string">&#39;%s&#39;</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="in-\u5E76\u884C\u7684\u7CBE\u786E\u5339\u914D" tabindex="-1"><a class="header-anchor" href="#in-\u5E76\u884C\u7684\u7CBE\u786E\u5339\u914D" aria-hidden="true">#</a> IN \u5E76\u884C\u7684\u7CBE\u786E\u5339\u914D</h3><p>\u7C7B\u4F3C\u4E8E\u4F7F\u7528 <code>WHERE</code> \u548C <code>=</code> \u7684\u8FD0\u7B97\u8FDB\u884C\u7CBE\u786E\u5339\u914D\uFF0C\u5982\u679C\u9700\u8981\u57FA\u4E8E\u540C\u4E00\u4E2A\u5B57\u6BB5\uFF08\u5217\uFF09\u8BBE\u7F6E\u591A\u4E2A\u7B5B\u9009\u6761\u4EF6\uFF0C\u53EF\u7528 <code>IN</code> \u8FD0\u7B97\u7B26\uFF0C\u591A\u4E2A\u6761\u4EF6\u4E4B\u95F4\u4F7F\u7528\u9017\u53F7 <code>,</code> \u5206\u9694\u3002</p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> name<span class="token punctuation">,</span> primary_poc<span class="token punctuation">,</span> sales_rep_id
<span class="token keyword">FROM</span> accounts
<span class="token comment">-- \u7B5B\u9009\u51FA\u5B57\u6BB5 name \u662F Walmart\u3001Target\u3001Nordstorm \u4E4B\u4E00\u7684\u6570\u636E</span>
<span class="token keyword">WHERE</span> name <span class="token operator">IN</span> <span class="token punctuation">(</span><span class="token string">&#39;Walmart&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;Target&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;Nordstrom&#39;</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="not" tabindex="-1"><a class="header-anchor" href="#not" aria-hidden="true">#</a> NOT</h3><p><code>NOT</code> \u8FD0\u7B97\u7B26\u67E5\u627E\u5230\u6240\u6709<strong>\u4E0D\u7B26\u5408</strong>\u7279\u5B9A\u6761\u4EF6\u7684\u884C\uFF0C\u4E00\u822C\u4E0E <code>IN</code> \u6216 <code>LIKE</code> \u4E00\u8D77\u4F7F\u7528\uFF0C\u5373 <code>NOT LIKE</code> \u6216 <code> NOT IN</code> \u8FD4\u56DE<strong>\u9664\u53BB</strong>\u4E0D\u7B26\u5408\u7279\u5B9A\u6761\u4EF6\u540E<strong>\u5269\u4F59</strong>\u7684\u6570\u636E\u3002</p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> name<span class="token punctuation">,</span> primary_poc<span class="token punctuation">,</span> sales_rep_id
<span class="token keyword">FROM</span> accounts
<span class="token comment">-- \u7B5B\u9009\u51FA\u5B57\u6BB5 name \u4E2D\u9664 Walmart\u3001Target \u548C Nordstrom \u4E4B\u5916\u7684\u6570\u636E</span>
<span class="token keyword">WHERE</span> name <span class="token operator">NOT</span> <span class="token operator">IN</span> <span class="token punctuation">(</span><span class="token string">&#39;Walmart&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;Target&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;Nordstrom&#39;</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="and-between" tabindex="-1"><a class="header-anchor" href="#and-between" aria-hidden="true">#</a> AND &amp; BETWEEN</h3><p>\u53EF\u7528\u4E8E\u7EC4\u5408\u6240\u6709\u6761\u4EF6\u5FC5\u987B\u4E3A<strong>\u771F</strong>\u7684\u8FD0\u7B97</p><h4 id="and" tabindex="-1"><a class="header-anchor" href="#and" aria-hidden="true">#</a> AND</h4><p>\u7528\u4E8E\u4E00\u6B21\u8003\u8651\u591A\u4E2A\u903B\u8F91\u5B50\u53E5\uFF0C\u7B5B\u9009\u51FA<strong>\u540C\u65F6\u6EE1\u8DB3</strong>\u591A\u4E2A\u6761\u4EF6\u7684\u6570\u636E</p><p>\u26A0\uFE0F <code>AND</code> \u8FDE\u63A5\u7684\u662F\u4E00\u4E2A\u65B0\u7684\u5B8C\u6574\u8BED\u53E5\uFF0C\u4E0D\u80FD\u5C06\u5176\u770B\u4F5C\u662F\u90E8\u5206\u6761\u4EF6\u7684\u8FDE\u63A5\u7B26\u3002</p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> <span class="token operator">*</span>
<span class="token keyword">FROM</span> orders
<span class="token keyword">WHERE</span> standard_qty <span class="token operator">&gt;</span> <span class="token number">1000</span> <span class="token operator">AND</span> poster_qty <span class="token operator">=</span> <span class="token number">0</span> <span class="token operator">AND</span> gloss_qty <span class="token operator">=</span> <span class="token number">0</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h4 id="between" tabindex="-1"><a class="header-anchor" href="#between" aria-hidden="true">#</a> BETWEEN</h4><p>\u7528 <code>AND</code> \u8FDE\u63A5\u4E0D\u540C\u8BED\u53E5\u65F6\uFF0C\u5982\u679C\u7B5B\u9009\u6761\u4EF6\u662F\u57FA\u4E8E\u76F8\u540C\u7684\u5B57\u6BB5\uFF0C\u6EE1\u8DB3\u4E00\u4E2A\u53D6\u503C\u8303\u56F4\u65F6\uFF0C\u53EF\u4EE5\u4F7F\u7528 <code>BETWEEN</code> \u66F4\u6E05\u695A</p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> <span class="token operator">*</span>
<span class="token keyword">FROM</span> table_name
<span class="token keyword">WHERE</span> <span class="token keyword">column</span> <span class="token operator">&gt;=</span> <span class="token number">6</span> <span class="token operator">AND</span> <span class="token keyword">column</span> <span class="token operator">&lt;=</span> <span class="token number">10</span>

<span class="token comment">-- \u4EE5\u4E0B\u7B5B\u9009\u6548\u679C\u76F8\u540C\uFF0C\u4F46\u8BED\u53E5\u66F4\u7B80\u6D01</span>
<span class="token comment">/* ... */</span>
<span class="token keyword">WHERE</span> <span class="token keyword">column</span> <span class="token operator">BETWEEN</span> <span class="token number">6</span> <span class="token operator">AND</span> <span class="token number">10</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><p>\u26A0\uFE0F \u5BF9\u4E8E\u5355\u7EAF\u7684\u65E5\u671F\u6570\u636E\u800C\u8A00\uFF08\u53EA\u5305\u542B\u5E74\u6708\u65E5\u4E0D\u5305\u542B\u65F6\u95F4\u7684\u6570\u636E\uFF0C\u6BD4\u5982 &#39;2016-12-31&#39;\uFF09\uFF0C\u5176\u9ED8\u8BA4\u65F6\u95F4\u4E3A\u65E5\u671F\u5F53\u5929\u7684 00:00:00\uFF08\u4E5F\u5C31\u662F\u5348\u591C\u65F6\u5206\uFF09\uFF0C\u800C\u4F7F\u7528\u8BED\u53E5 <code>BETWEEN</code> \u8BBE\u7F6E\u65F6\u95F4\u7B5B\u9009\u6761\u4EF6\u65F6\uFF0C\u5B83\u662F<strong>\u4E0D\u5305\u542B\u7AEF\u70B9</strong>\u7684\uFF0C\u56E0\u6B64\u5982\u679C\u9700\u8981\u67E5\u8BE2 <code>2016\u5E74</code> \u7684\u6570\u636E\uFF0C\u5219\u5E94\u8BE5\u4F7F\u7528</p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token operator">BETWEEN</span> <span class="token string">&#39;2016-01-01&#39;</span> <span class="token operator">AND</span> <span class="token string">&#39;2017-01-01&#39;</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br></div></div><h3 id="or" tabindex="-1"><a class="header-anchor" href="#or" aria-hidden="true">#</a> OR</h3><p>\u7528 <code>OR</code> \u7EC4\u5408<strong>\u81F3\u5C11\u4E00\u4E2A\u6761\u4EF6\u4E3A\u771F</strong>\u7684\u8FD0\u7B97\uFF0C\u7528\u4E8E\u4E00\u6B21\u8003\u8651\u591A\u4E2A\u903B\u8F91\u5B50\u53E5\uFF0C\u53EA\u9700\u8981\u6EE1\u8DB3\u5176\u4E2D\u4E00\u4E2A\u5373\u53EF\u3002</p><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token keyword">SELECT</span> id<span class="token punctuation">,</span> gloss_qty<span class="token punctuation">,</span> poster_qty
<span class="token keyword">FROM</span> orders
<span class="token keyword">WHERE</span> gloss_qty <span class="token operator">&gt;</span> <span class="token number">4000</span> <span class="token operator">OR</span> poster_qty <span class="token operator">&gt;</span> <span class="token number">4000</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>\u26A0\uFE0F \u4F7F\u7528 OR \u8FDE\u63A5\u7684\u4E5F\u662F\u65B0\u7684\u5B8C\u6574\u7684\u8BED\u53E5</p><h2 id="\u6CE8\u91CA" tabindex="-1"><a class="header-anchor" href="#\u6CE8\u91CA" aria-hidden="true">#</a> \u6CE8\u91CA</h2><p>SQL \u6CE8\u91CA\u7684\u8BED\u6CD5</p><ul><li>\u5355\u884C\u6CE8\u91CA\uFF1A\u4F7F\u7528 <code>--</code>\uFF08\u540E\u8DDF\u6709\u4E00\u4E2A\u7A7A\u683C\uFF09</li><li>\u591A\u884C\u6CE8\u91CA\uFF1A\u4F7F\u7528 <code>/* */</code></li></ul><div class="language-sql ext-sql line-numbers-mode"><pre class="language-sql"><code><span class="token comment">/*
\u6B64\u5904\u4E3A\u6CE8\u91CA....
*/</span>
<span class="token keyword">select</span> <span class="token operator">*</span> <span class="token keyword">from</span> students<span class="token punctuation">;</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div>`,74);function o(c,d){return e}var p=n(a,[["render",o]]);export{p as default};
