import{c as e}from"./app.43fe0c63.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";var a="/blog-web/assets/20201028233648996_31134.1d6cc01e.png",o="/blog-web/assets/20201029000708364_8016.815b0cdf.png",c="/blog-web/assets/20201029001254149_100.09a0f437.png",l="/blog-web/assets/20201029002257549_32719.eeb1c4fe.png",i="/blog-web/assets/20201029002103142_412.b29f9774.png",t="/blog-web/assets/20201029111455398_473.bcff7053.png";const r={},s=e('<h1 id="mysql-\u57FA\u7840\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#mysql-\u57FA\u7840\u64CD\u4F5C" aria-hidden="true">#</a> MySQL \u57FA\u7840\u64CD\u4F5C</h1><p>\u53C2\u8003\uFF1A<a href="https://devhints.io/mysql" target="_blank" rel="noopener noreferrer">MySQL cheatsheet</a></p><p>\u53EF\u4EE5\u901A\u8FC7 MySQL Command Line Client \u5728\u547D\u4EE4\u884C\u754C\u9762\u64CD\u4F5C\u6570\u636E\u5E93\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u7528 MySQL Workbench \u4F7F\u7528 GUI \u53EF\u89C6\u5316\u754C\u9762\u64CD\u4F5C\u6570\u636E\u5E93\u3002</p><h2 id="\u6CE8\u91CA" tabindex="-1"><a class="header-anchor" href="#\u6CE8\u91CA" aria-hidden="true">#</a> \u6CE8\u91CA</h2><p><code>-- comments</code> \u6DFB\u52A0\u6CE8\u91CA</p><h2 id="\u64CD\u4F5C\u6570\u636E\u5E93" tabindex="-1"><a class="header-anchor" href="#\u64CD\u4F5C\u6570\u636E\u5E93" aria-hidden="true">#</a> \u64CD\u4F5C\u6570\u636E\u5E93</h2><ul><li><p><code>SHOW DATABASES;</code> \u67E5\u8BE2\u6240\u6709\u6570\u636E\u5E93</p></li><li><p><code>CREATE SCHEMA databaseName;</code> \u6216 <code>CREATE DATABASE databasename;</code> \u521B\u5EFA\u6570\u636E\u5E93</p><p>\u4EE5\u4E0B\u56FE\u793A\u4F7F\u7528 MySQL Workbench \u521B\u5EFA\u6570\u636E\u5E93</p></li></ul><p><img src="'+a+'" alt="\u4F7F\u7528 MySQL Workbench \u5EFA\u5E93"></p><p>\u{1F4A1} \u53EF\u4EE5\u8BBE\u7F6E\u6570\u636E\u7F16\u7801\u65B9\u5F0F <code>CREATE DATABASE databaseName CHARACTER SET utf-8;</code> \u6216\u4F7F\u7528 <code>ALTER DATABASE databaseName CHARACTER SET utf-8</code> \u5207\u6362\u6570\u636E\u7F16\u7801\u65B9\u5F0F</p><ul><li><p><code>USE databaseName</code> \u5207\u6362\u6570\u636E\u5E93</p><p>\u26A0\uFE0F \u5728\u64CD\u4F5C\u6570\u636E\u4E4B\u524D\u90FD\u9700\u8981<strong>\u5148\u6307\u5B9A\u8FDE\u63A5\u5230\u54EA\u4E2A\u6570\u636E\u5E93</strong></p></li><li><p><code>DROP DATABASE databaseName</code> \u5220\u9664\u6570\u636E\u5E93</p></li></ul><h2 id="\u64CD\u4F5C\u8868\u683C" tabindex="-1"><a class="header-anchor" href="#\u64CD\u4F5C\u8868\u683C" aria-hidden="true">#</a> \u64CD\u4F5C\u8868\u683C</h2><ul><li><p><code>SHOW TABLES;</code> \u67E5\u770B\u6570\u636E\u5E93\u6240\u6709\u8868\u683C</p></li><li><p><code>CREATE TABLE table (field1 type1, field2 type2, PRIMARY KEY (field1));</code> \u521B\u5EFA\u8868\u683C\uFF0C<strong>\u9700\u8981\u6307\u5B9A\u6BCF\u4E2A\u5B57\u6BB5\u7684\u6570\u636E\u7C7B\u578B</strong></p><p>\u4EE5\u4E0B\u56FE\u793A\u4F7F\u7528 MySQL Workbench \u521B\u5EFA\u8868\u683C</p></li></ul><p><img src="'+o+'" alt="\u4F7F\u7528 MySQL Workbench \u5EFA\u8868"></p><p><img src="'+c+'" alt="\u793A\u4F8B\u8868\u683C\u5404\u5B57\u6BB5\u7684\u914D\u7F6E"></p><ul><li><p>\u4FEE\u6539\u8868</p><ul><li><code>ALTER TABLE tableName MODIFY field1 type1</code> \u4FEE\u6539\u539F\u6709\u7684\u5B57\u6BB5\u6570\u636E\u7C7B\u578B</li><li><code>ALTER TABLE tableName CHANGE old_name_field1 new_name_field1 type1</code> \u4FEE\u6539\u539F\u6709\u7684\u5B57\u6BB5\u540D\u79F0</li><li><code>ALTER TABLE tableName ADD new_name_field1 type1</code> \u6DFB\u52A0\u65B0\u5B57\u6BB5</li><li><code>ALTER TABLE tableName DROP field1</code> \u5220\u9664\u5DF2\u6709\u5B57\u6BB5</li></ul><p>\u4EE5\u4E0B\u56FE\u793A\u4F7F\u7528 MySQL Workbench \u4FEE\u6539\u8868\u683C</p></li></ul><p><img src="'+l+'" alt="\u4F7F\u7528 MySQL Workbench \u4FEE\u6539\u8868"></p><ul><li><p><code>DROP TABLE tableName</code> \u5220\u9664\u8868</p><p>\u4EE5\u4E0B\u56FE\u793A\u4F7F\u7528 MySQL Workbench \u5220\u9664\u8868\u683C</p><p><img src="'+i+`" alt="\u4F7F\u7528 MySQL Workbench \u5220\u9664\u8868"></p></li></ul><h2 id="\u64CD\u4F5C\u6570\u636E" tabindex="-1"><a class="header-anchor" href="#\u64CD\u4F5C\u6570\u636E" aria-hidden="true">#</a> \u64CD\u4F5C\u6570\u636E</h2><p>\u5148\u4F7F\u7528\u8BED\u53E5 <code>USE databaseName;</code> \u5207\u6362\u5230\u9700\u8981\u64CD\u4F5C\u7684\u6570\u636E\u5E93</p><h3 id="\u65B0\u589E" tabindex="-1"><a class="header-anchor" href="#\u65B0\u589E" aria-hidden="true">#</a> \u65B0\u589E</h3><ul><li><code>INSERT INTO table1 (field1, field2) VALUES (value1, value2);</code> \u5411\u8868\u683C\u6DFB\u52A0\u6570\u636E\u3002\u{1F4A1} \u5BF9\u4E8E\u5173\u952E\u5B57\uFF0C\u5982 <code>password</code> \u5E94\u4F7F\u7528\u53CD\u5355\u5F15\u53F7\u5305\u62EC <code>fieldName</code></li></ul><h3 id="\u67E5\u8BE2" tabindex="-1"><a class="header-anchor" href="#\u67E5\u8BE2" aria-hidden="true">#</a> \u67E5\u8BE2</h3><ul><li><p><code>SELECT * FROM table;</code> \u67E5\u8BE2\u8868\u683C\u6570\u636E\uFF0C\u8FD4\u56DE\u6240\u6709\u8868\u683C\u7684\u6570\u636E\uFF0C\u5305\u542B\u6BCF\u4E00\u884C\u548C\u6BCF\u4E00\u4E2A\u5B57\u6BB5\u7684\u6570\u636E</p></li><li><p><code>SELECT field1, field2, ... FROM table;</code> \u67E5\u8BE2\u8868\u683C\u6570\u636E\uFF0C\u4E14\u53EA\u8FD4\u56DE\u6307\u5B9A\u7684\u5B57\u6BB5</p></li><li><p><code>SELECT * FROM talbe WHERE condition</code> \u57FA\u4E8E\u6761\u4EF6\u67E5\u8BE2\u8868\u683C\u6570\u636E</p><ul><li><p>\u5728 <code>condition</code> \u4E2D\u6700\u5E38\u4F7F\u7528\u7B49\u4E8E <code>=</code> \u6216\u4E0D\u7B49\u4E8E <code>&lt;&gt;</code> \u6784\u5EFA\u6761\u4EF6 <code>SELECT * FROM table WHERE field=&#39;value&#39;;</code>\uFF1B\u5982\u679C\u9700\u8981\u6A21\u7CCA\u5339\u914D\uFF0C\u53EF\u4EE5\u4F7F\u7528 <code>LIKE</code> \u5E76\u7ED3\u5408\u6B63\u5219\u8868\u8FBE\u5F0F <code>SELECT * FROM table WHERE field LIKE &#39;%keyword%&#39;;</code></p><p>\u{1F4A1} <code>%</code> \u9002\u914D\u4EFB\u610F\u6570\u91CF\u7684\u5B57\u7B26\uFF0C\u53EF\u4EE5\u4EA7\u751F\u4E00\u7EC4\u7279\u5B9A\u7684\u5339\u914D\u6A21\u5F0F</p></li><li><p>\u5728 <code>condition</code> \u4E2D\u53EF\u4EE5\u4F7F\u7528 <code>AND</code> \u6784\u5EFA\u591A\u4E2A\u6761\u4EF6\u5E76\u5217\u6210\u7ACB\u7684\u590D\u6742\u6761\u4EF6</p></li><li><p>\u5728 <code>condition</code> \u4E2D\u53EF\u4EE5\u4F7F\u7528 <code>OR</code> \u6784\u5EFA\u591A\u4E2A\u6761\u4EF6\u4E4B\u4E00\u6210\u7ACB\u7684\u590D\u6742\u6761\u4EF6</p></li><li><p>\u5728 <code>condition</code> \u4E2D\u4F7F\u7528 <code>IS Null</code> \u6216 <code>IS NOT NULL</code> \u6765\u5206\u522B\u67E5\u8BE2\u5B57\u6BB5\u503C<strong>\u662F\u5426\u4E3A\u7A7A</strong> <code>SELECT * FROM table WHERE field IS NOT NULL;</code></p></li></ul></li><li><p><code>SELECT field FROM table ORDER BY field1, field2;</code> \u67E5\u8BE2\u7ED3\u679C\u6309\u7167\u5B57\u6BB5\u6392\u5E8F\u3002\u9ED8\u8BA4\u5347\u5E8F\uFF0C\u5982\u679C\u9700\u8981\u964D\u5E8F\u53EF\u4EE5\u6DFB\u52A0\u5173\u952E\u5B57 <code>DESC</code></p></li></ul><h3 id="\u66F4\u65B0" tabindex="-1"><a class="header-anchor" href="#\u66F4\u65B0" aria-hidden="true">#</a> \u66F4\u65B0</h3><ul><li><code>UPDATE table SET field=new_value WHERE condition;</code> \u66F4\u65B0\u6307\u5B9A\u8BB0\u5F55\uFF08\u884C\u6570\u636E\uFF09\u7684\u6307\u5B9A\u5B57\u6BB5\u7684\u503C</li></ul><h3 id="\u5220\u9664" tabindex="-1"><a class="header-anchor" href="#\u5220\u9664" aria-hidden="true">#</a> \u5220\u9664</h3><ul><li><code>DELETE FROM table WHERE condition;</code> \u5220\u9664\u6307\u5B9A\u8BB0\u5F55\uFF08\u884C\u6570\u636E\uFF09</li></ul><p>\u{1F4A1} \u4E00\u822C\u4E0D\u4F7F\u7528 <code>DELETE FROM</code> \u8BED\u53E5\uFF0C\u56E0\u4E3A\u76F8\u5E94\u7684\u6570\u636E\u4F1A\u5B8C\u5168\u5730\u4ECE\u6570\u636E\u5E93\u53BB\u9664\u3002\u800C\u662F\u4E3A\u6BCF\u4E2A\u8BB0\u5F55\uFF08\u884C\u6570\u636E\uFF09\u6DFB\u52A0\u4E00\u4E2A\u8868\u793A\u72B6\u6001 <code>state</code> \u7684\u5B57\u6BB5\uFF0C\u901A\u8FC7 <code>UPDATE</code> \u8BED\u53E5\u6765\u5207\u6362\u8BB0\u5F55\u7684\u72B6\u6001\uFF0C\u4E00\u822C\u4EE5 <code>0</code> \u6216 <code>1</code> \u8868\u793A\u8BB0\u5F55\u662F\u5426\u4E3A<strong>\u53EF\u8BBF\u95EE</strong>\u7684\u72B6\u6001\uFF0C\u901A\u8FC7\u8FD9\u79CD\u5F62\u5F0F\u5B9E\u73B0\u300C<strong>\u8F6F\u5220\u9664</strong>\u300D\uFF0C\u7136\u540E\u5728\u67E5\u8BE2\u65F6\u8BBE\u7F6E\u57FA\u4E8E <code>state</code> \u6761\u4EF6 <code>SELECT * FROM table WHERE state&lt;&gt;&#39;0&#39;;</code>\uFF0C\u8FD9\u79CD\u5220\u9664\u65B9\u5F0F\u53EF\u4EE5\u65B9\u4FBF\u5730\u5B9E\u73B0\u6570\u636E\u6062\u590D\u3002</p><h2 id="\u5176\u4ED6" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6" aria-hidden="true">#</a> \u5176\u4ED6</h2><p>\u{1F4A1} \u5728\u66F4\u65B0\u6216\u5220\u9664\u6570\u636E\u65F6\u53EF\u80FD\u4F1A\u9047\u5230\u4EE5\u4E0B\u9519\u8BEF</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>Error Code: <span class="token number">1175</span>. You are using safe update mode and you tried to update a table without a WHERE that uses a KEY column.  To disable safe mode, toggle the option <span class="token keyword">in</span> Preferences -<span class="token operator">&gt;</span> SQL Editor and reconnect.
</code></pre><div class="line-numbers"><span class="line-number">1</span><br></div></div><p>\u53EF\u4EE5\u5728 sql \u811A\u672C\u4E2D\u6267\u884C <code>SET SQL_SAFE_UPDATES = 0</code> \u6216\u6839\u636E\u9519\u8BEF\u4FE1\u606F\u63D0\u793A\u4FEE\u6539 MySQL \u914D\u7F6E\uFF0C\u5C06\u5B89\u5168\u6A21\u5F0F\u5173\u95ED\u3002</p><p><img src="`+t+'" alt="\u53D6\u6D88\u300C\u7F16\u8F91\u5B89\u5168\u6A21\u5F0F\u300D\uFF0C\u5E76\u91CD\u65B0\u8FDE\u63A5\u6570\u636E\u5E93"></p>',33);function n(p,E){return s}var u=d(r,[["render",n]]);export{u as default};
