import{c as t}from"./app.43fe0c63.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const o={},r=t('<h1 id="nosql-\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#nosql-\u7B80\u4ECB" aria-hidden="true">#</a> NoSQL \u7B80\u4ECB</h1><p>\u53C2\u8003\uFF1A<a href="https://www.mongodb.org.cn/tutorial/2.html" target="_blank" rel="noopener noreferrer">NoSQL \u7B80\u4ECB</a></p><h2 id="nosql" tabindex="-1"><a class="header-anchor" href="#nosql" aria-hidden="true">#</a> NoSQL</h2><p>NoSQL \u662F Not Only SQL \u7684\u7F29\u5199\uFF0C\u610F\u5373\u4E0D\u4EC5\u4EC5\u662F SQL\uFF0C\u4E00\u822C\u6307\u4F7F\u7528<strong>\u975E\u5173\u7CFB\u578B</strong>\u7684\u6570\u636E\u5B58\u50A8\u6280\u672F\u3002NoSQL\u4E00\u8BCD\u6700\u65E9\u51FA\u73B0\u4E8E 1998 \u5E74\uFF0C\u662F Carlo Strozzi \u5F00\u53D1\u7684\u4E00\u4E2A\u8F7B\u91CF\u3001\u5F00\u6E90\u3001\u4E0D\u63D0\u4F9B SQL \u529F\u80FD\u7684\u5173\u7CFB\u6570\u636E\u5E93\u3002</p><p>\u5BF9 NoSQL \u6700\u666E\u904D\u7684\u89E3\u91CA\u662F\u300C\u975E\u5173\u8054\u578B\u7684\u300D\uFF0C\u5F3A\u8C03 <mark>Key-Value</mark> Stores \u548C\u6587\u6863\u6570\u636E\u5E93\u7684\u4F18\u70B9\uFF0C\u800C\u4E0D\u662F\u5355\u7EAF\u7684\u53CD\u5BF9 RDBMS\u3002</p><p>\u{1F4A1} NoSQL \u9075\u987C BASE \u539F\u5219\uFF0C\u800C SQL \u9075\u5FAA ACID \u539F\u5219</p><ul><li><p>BASE \u662F NoSQL \u6570\u636E\u5E93\u901A\u5E38\u5BF9\u53EF\u7528\u6027\u53CA\u4E00\u81F4\u6027\u7684\u5F31\u8981\u6C42\u539F\u5219\uFF1A</p><ul><li>Basically Availble \u57FA\u672C\u53EF\u7528</li><li>Soft State \u8F6F\u72B6\u6001/\u67D4\u6027\u4E8B\u52A1\u3002Soft state \u53EF\u4EE5\u7406\u89E3\u4E3A\u300C\u65E0\u8FDE\u63A5\u300D\u7684\u72B6\u6001\uFF0C\u800C Hard state \u662F\u300C\u9762\u5411\u8FDE\u63A5\u300D\u7684</li><li>Eventual Consistency \u6700\u7EC8\u4E00\u81F4\u6027\uFF0C\u4E5F\u662F ACID \u7684\u6700\u7EC8\u76EE\u7684</li></ul></li><li><p><a href="https://www.mongodb.org.cn/tutorial/2.html" target="_blank" rel="noopener noreferrer">ACID \u89C4\u5219</a> \u662F\u5173\u7CFB\u578B\u6570\u636E\u5E93\u9075\u5FAA\u7684\u539F\u5219\uFF1A</p><ul><li>A\uFF1A\u539F\u5B50\u6027 Atomicity \u662F\u6307\u4E8B\u52A1\uFF08transaction \u548C\u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u4EA4\u6613\u5F88\u7C7B\u4F3C\uFF09\u91CC\u7684\u6240\u6709\u64CD\u4F5C\u8981\u4E48\u5168\u90E8\u505A\u5B8C\uFF0C\u8981\u4E48\u90FD\u4E0D\u505A\uFF0C\u4E8B\u52A1\u6210\u529F\u7684\u6761\u4EF6\u662F\u4E8B\u52A1\u91CC\u7684\u6240\u6709\u64CD\u4F5C\u90FD\u6210\u529F\uFF0C\u53EA\u8981\u6709\u4E00\u4E2A\u64CD\u4F5C\u5931\u8D25\uFF0C\u6574\u4E2A\u4E8B\u52A1\u5C31\u5931\u8D25\uFF0C\u9700\u8981\u56DE\u6EDA\u3002</li><li>C\uFF1A\u4E00\u81F4\u6027 Consistency \u662F\u6307\u4E8B\u52A1\u7684\u8FD0\u884C\u4E0D\u4F1A\u6539\u53D8\u6570\u636E\u5E93\u539F\u672C\u7684\u4E00\u81F4\u6027\u7EA6\u675F\uFF0C\u6570\u636E\u5E93\u8981\u4E00\u76F4\u5904\u4E8E\u4E00\u81F4\u7684\u72B6\u6001\u3002</li><li>I\uFF1A\u72EC\u7ACB\u6027 Isolation \u662F\u6307\u5E76\u53D1\u7684\u4E8B\u52A1\u4E4B\u95F4\u4E0D\u4F1A\u4E92\u76F8\u5F71\u54CD\uFF0C\u5982\u679C\u4E00\u4E2A\u4E8B\u52A1\u8981\u8BBF\u95EE\u7684\u6570\u636E\u6B63\u5728\u88AB\u53E6\u5916\u4E00\u4E2A\u4E8B\u52A1\u4FEE\u6539\uFF0C\u53EA\u8981\u53E6\u5916\u4E00\u4E2A\u4E8B\u52A1\u672A\u63D0\u4EA4\uFF0C\u5B83\u6240\u8BBF\u95EE\u7684\u6570\u636E\u5C31\u4E0D\u53D7\u672A\u63D0\u4EA4\u4E8B\u52A1\u7684\u5F71\u54CD\u3002</li><li>D\uFF1A\u6301\u4E45\u6027 Durability \u662F\u6307\u4E00\u65E6\u4E8B\u52A1\u63D0\u4EA4\u540E\uFF0C\u5B83\u6240\u505A\u7684\u4FEE\u6539\u5C06\u4F1A\u6C38\u4E45\u7684\u4FDD\u5B58\u5728\u6570\u636E\u5E93\u4E0A\uFF0C\u5373\u4F7F\u51FA\u73B0\u5B95\u673A\u4E5F\u4E0D\u4F1A\u4E22\u5931\u3002</li></ul></li></ul><table><thead><tr><th style="text-align:center;">ACID</th><th style="text-align:center;">BASE</th></tr></thead><tbody><tr><td style="text-align:center;">\u539F\u5B50\u6027(<strong>A</strong>tomicity)</td><td style="text-align:center;">\u57FA\u672C\u53EF\u7528(<strong>B</strong>asically <strong>A</strong>vailable)</td></tr><tr><td style="text-align:center;">\u4E00\u81F4\u6027(<strong>C</strong>onsistency)</td><td style="text-align:center;">\u8F6F\u72B6\u6001/\u67D4\u6027\u4E8B\u52A1(<strong>S</strong>oft state)</td></tr><tr><td style="text-align:center;">\u9694\u79BB\u6027(<strong>I</strong>solation)</td><td style="text-align:center;">\u6700\u7EC8\u4E00\u81F4\u6027 (<strong>E</strong>ventual consistency)</td></tr><tr><td style="text-align:center;">\u6301\u4E45\u6027 (<strong>D</strong>urable)</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="nosql-\u6570\u636E\u5E93\u5206\u7C7B" tabindex="-1"><a class="header-anchor" href="#nosql-\u6570\u636E\u5E93\u5206\u7C7B" aria-hidden="true">#</a> NoSQL \u6570\u636E\u5E93\u5206\u7C7B</h2><ul><li>\u5217\u5B58\u50A8\uFF1A\u6309\u5217\u5B58\u50A8\u6570\u636E\u7684\u3002\u6700\u5927\u7684\u7279\u70B9\u662F\u65B9\u4FBF\u5B58\u50A8\u7ED3\u6784\u5316\u548C\u534A\u7ED3\u6784\u5316\u6570\u636E\uFF0C\u65B9\u4FBF\u505A\u6570\u636E\u538B\u7F29\uFF0C\u5982 Hbase</li><li>\u6587\u6863\u5B58\u50A8\uFF1A\u6587\u6863\u5B58\u50A8\u4E00\u822C\u7528\u7C7B\u4F3C json \u7684\u683C\u5F0F\u5B58\u50A8\uFF0C\u5B58\u50A8\u7684\u5185\u5BB9\u662F\u6587\u6863\u578B\u7684\u3002\u8FD9\u6837\u4E5F\u5C31\u6709\u673A\u4F1A\u5BF9\u67D0\u4E9B\u5B57\u6BB5\u5EFA\u7ACB\u7D22\u5F15\uFF0C\u5B9E\u73B0\u5173\u7CFB\u6570\u636E\u5E93\u7684\u67D0\u4E9B\u529F\u80FD\uFF0C\u5982 MongoDB</li><li>key-value \u5B58\u50A8\uFF1A\u53EF\u4EE5\u901A\u8FC7 key \u5FEB\u901F\u67E5\u8BE2\u5230\u5176 value\u3002\u4E00\u822C\u6765\u8BF4\uFF0C\u5B58\u50A8\u4E0D\u7BA1 value \u7684\u683C\u5F0F\uFF0C\u7167\u5355\u5168\u6536\uFF0C\u5982 Redis</li><li>\u56FE\u5B58\u50A8\uFF1A\u56FE\u5F62\u5173\u7CFB\u7684\u6700\u4F73\u5B58\u50A8\uFF0C\u5982 FlockDB</li><li>\u5BF9\u8C61\u5B58\u50A8\uFF1A\u901A\u8FC7\u7C7B\u4F3C\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u7684\u8BED\u6CD5\u64CD\u4F5C\u6570\u636E\u5E93\uFF0C\u901A\u8FC7\u5BF9\u8C61\u7684\u65B9\u5F0F\u5B58\u53D6\u6570\u636E\uFF0C\u5982 db4o</li><li>xml \u6570\u636E\u5E93\uFF1A\u9AD8\u6548\u7684\u5B58\u50A8 XML \u6570\u636E\uFF0C\u5E76\u652F\u6301 XML \u7684\u5185\u90E8\u67E5\u8BE2\u8BED\u6CD5\uFF0C\u5982 XQuery</li></ul><h2 id="mongodb" tabindex="-1"><a class="header-anchor" href="#mongodb" aria-hidden="true">#</a> MongoDB</h2><p><a href="https://www.mongodb.com/" target="_blank" rel="noopener noreferrer">MongoDB</a> \u662F\u7531 C++ \u8BED\u8A00\u7F16\u5199\u7684 NoSQL\uFF0C\u662F\u4E00\u4E2A\u57FA\u4E8E\u5206\u5E03\u5F0F\u6587\u4EF6\u5B58\u50A8\u7684\u5F00\u6E90\u6570\u636E\u5E93\u7CFB\u7EDF\uFF0C\u65E8\u5728\u4E3A WEB \u5E94\u7528\u63D0\u4F9B\u53EF\u6269\u5C55\u7684\u9AD8\u6027\u80FD\u6570\u636E\u5B58\u50A8\u89E3\u51B3\u65B9\u6848\u3002</p><p>MongoDB \u5C06\u6570\u636E\u5B58\u50A8\u4E3A\u4E00\u4E2A\u6587\u6863\uFF0C\u5176\u6570\u636E\u7ED3\u6784\u7531\u952E\u503C <code>key: value</code> \u5BF9\u7EC4\u6210\uFF0C\u7C7B\u4F3C\u4E8E JSON \u5BF9\u8C61\uFF0C\u5B57\u6BB5\u503C <code>value</code> \u53EF\u4EE5\u5305\u542B\u5176\u4ED6\u6587\u6863\uFF0C\u6570\u7EC4\u53CA\u6587\u6863\u6570\u7EC4\u7B49\u591A\u79CD\u6570\u636E\u7C7B\u578B\u3002</p><p>\u{1F4A1} <a href="https://mongoosejs.com/" target="_blank" rel="noopener noreferrer">Mongoose</a> \u662F\u4E00\u4E2A\u57FA\u4E8E MongoDB \u7684 <strong>Nodejs \u6A21\u5757</strong>\uFF0C\u7ED3\u5408\u4E86 NoSQL \u548C SQL \u7684\u7279\u70B9\u4E3A\u6A21\u578B\u63D0\u4F9B\u4E86\u4E00\u79CD\u76F4\u63A5\u7684\uFF0C<strong>\u57FA\u4E8E scheme \u7ED3\u6784\u53BB\u5B9A\u4E49\u6570\u636E\u6A21\u578B</strong>\u3002\u5B83\u5185\u7F6E\u6570\u636E\u9A8C\u8BC1\uFF0C \u67E5\u8BE2\u6784\u5EFA\uFF0C\u4E1A\u52A1\u903B\u8F91\u94A9\u5B50\u7B49\uFF0C\u5F00\u7BB1\u5373\u7528\u3002</p>',14);function n(l,a){return r}var d=e(o,[["render",n]]);export{d as default};
